preferences.postscript:
if (Translator.BetterBibTeX && item.itemType === 'conferencePaper' && item.extra) {
    const lines = item.extra.split('\n')
    for (let line of lines) {
        let matches = /^([A-Za-z0-9]+): *(.+)$/.exec(line)
        if (matches === null || matches[1] !== 'Sponsor') {
            continue
        }
        reference.add({name: 'organization', value: matches[2]})
    }
}

