preferences.postscript:
// Only use book as type, not collection
if (Translator.BetterBibLaTeX && item.referencetype === 'collection') item.referencetype = 'book'

// Duplicate title to shorttitle if empty
if (Translator.BetterBibLaTeX && !this.has.shorttitle) this.add({ name: 'shorttitle', value: item.title })

// Duplicate title to booktitle for books
if (Translator.BetterBibLaTeX && item.itemType === 'book') this.add({ name: 'booktitle', value: item.title })

// Set english as langid if empty
if (Translator.BetterBibLaTeX && !this.has.langid) this.add({ name: 'langid', value: 'english' })

// Only export year to date field
if (Translator.BetterBibLaTeX && item.date) {
  const date = Zotero.BetterBibTeX.parseDate(item.date)
  if (date.type === 'date') this.add({ name: 'date', value: date.year })
}

// Correct single hyphens in page field
if (Translator.BetterBibLaTeX) {
  if (this.has.pages) this.has.pages.bibtex = this.has.pages.bibtex.replace(/(\w+)-(\w+)/g, '$1--$2');
}
