// Add `pagetotal' field for `book' items in BibTeX bibliography
if (Translator.BetterBibTeX) {
	switch (item.itemType) {
		case 'book':
			this.add({ name: 'pagetotal', value: item.numPages });
			break;
	}
}

// https://github.com/retorquere/zotero-better-bibtex/issues/512#issuecomment-489435978
if (Translator.BetterTeX) { // covers both Translator.BetterBibTeX and Translator.BetterBibLaTeX
	var order = [
		'author',
		'title',
		'type', // techreport
		'institution', // techreport
		'journal',
		'editor',
		'booktitle',
		'edition',
		'series',
		'address',
		'publisher',
		'year',
		'volume',
		'number',
		'pages',
		'pagetotal',
		'note',
		'doi',
		'url',
		'isbn',
		'language',
		'abstract',
	];

	const keys = Object.keys(this.has)
	for (const field of keys.sort((a, b) => ((order.indexOf(a) + 1) || (keys.length + order.length + 1)) - ((order.indexOf(b) + 1) || (keys.length + order.length + 1)))) {
		const value = this.has[field]
		delete this.has[field]
		this.has[field] = value
	}
}
