function unalias(item) {
  // date
  if (typeof item.dateDecided !== 'undefined') { item.date = item.dateDecided; delete item.dateDecided }
  else if (jurism && typeof item.dateEnacted !== 'undefined') { item.date = item.dateEnacted; delete item.dateEnacted }
  else if (typeof item.dateEnacted !== 'undefined') { item.date = item.dateEnacted; delete item.dateEnacted }
  else if (typeof item.issueDate !== 'undefined') { item.date = item.issueDate; delete item.issueDate }

  // medium
  if (typeof item.artworkMedium !== 'undefined') { item.medium = item.artworkMedium; delete item.artworkMedium }
  else if (typeof item.audioFileType !== 'undefined') { item.medium = item.audioFileType; delete item.audioFileType }
  else if (typeof item.audioRecordingFormat !== 'undefined') { item.medium = item.audioRecordingFormat; delete item.audioRecordingFormat }
  else if (typeof item.interviewMedium !== 'undefined') { item.medium = item.interviewMedium; delete item.interviewMedium }
  else if (typeof item.videoRecordingFormat !== 'undefined') { item.medium = item.videoRecordingFormat; delete item.videoRecordingFormat }

  // number
  if (typeof item.billNumber !== 'undefined') { item.number = item.billNumber; delete item.billNumber }
  else if (typeof item.docketNumber !== 'undefined') { item.number = item.docketNumber; delete item.docketNumber }
  else if (typeof item.documentNumber !== 'undefined') { item.number = item.documentNumber; delete item.documentNumber }
  else if (typeof item.episodeNumber !== 'undefined') { item.number = item.episodeNumber; delete item.episodeNumber }
  else if (typeof item.patentNumber !== 'undefined') { item.number = item.patentNumber; delete item.patentNumber }
  else if (jurism && typeof item.publicLawNumber !== 'undefined') { item.number = item.publicLawNumber; delete item.publicLawNumber }
  else if (typeof item.publicLawNumber !== 'undefined') { item.number = item.publicLawNumber; delete item.publicLawNumber }
  else if (typeof item.reportNumber !== 'undefined') { item.number = item.reportNumber; delete item.reportNumber }

  // pages
  if (typeof item.codePages !== 'undefined') { item.pages = item.codePages; delete item.codePages }
  else if (typeof item.firstPage !== 'undefined') { item.pages = item.firstPage; delete item.firstPage }

  // publicationTitle
  if (typeof item.blogTitle !== 'undefined') { item.publicationTitle = item.blogTitle; delete item.blogTitle }
  else if (typeof item.bookTitle !== 'undefined') { item.publicationTitle = item.bookTitle; delete item.bookTitle }
  else if (jurism && typeof item.bookTitle !== 'undefined') { item.publicationTitle = item.bookTitle; delete item.bookTitle }
  else if (typeof item.dictionaryTitle !== 'undefined') { item.publicationTitle = item.dictionaryTitle; delete item.dictionaryTitle }
  else if (typeof item.encyclopediaTitle !== 'undefined') { item.publicationTitle = item.encyclopediaTitle; delete item.encyclopediaTitle }
  else if (typeof item.forumTitle !== 'undefined') { item.publicationTitle = item.forumTitle; delete item.forumTitle }
  else if (typeof item.proceedingsTitle !== 'undefined') { item.publicationTitle = item.proceedingsTitle; delete item.proceedingsTitle }
  else if (typeof item.programTitle !== 'undefined') { item.publicationTitle = item.programTitle; delete item.programTitle }
  else if (jurism && typeof item.reporter !== 'undefined') { item.publicationTitle = item.reporter; delete item.reporter }
  else if (jurism && typeof item.websiteTitle !== 'undefined') { item.publicationTitle = item.websiteTitle; delete item.websiteTitle }
  else if (typeof item.websiteTitle !== 'undefined') { item.publicationTitle = item.websiteTitle; delete item.websiteTitle }

  // publisher
  if (typeof item.company !== 'undefined') { item.publisher = item.company; delete item.company }
  else if (typeof item.distributor !== 'undefined') { item.publisher = item.distributor; delete item.distributor }
  else if (zotero && typeof item.institution !== 'undefined') { item.publisher = item.institution; delete item.institution }
  else if (typeof item.label !== 'undefined') { item.publisher = item.label; delete item.label }
  else if (typeof item.network !== 'undefined') { item.publisher = item.network; delete item.network }
  else if (typeof item.studio !== 'undefined') { item.publisher = item.studio; delete item.studio }
  else if (typeof item.university !== 'undefined') { item.publisher = item.university; delete item.university }

  // title
  if (typeof item.caseName !== 'undefined') { item.title = item.caseName; delete item.caseName }
  else if (jurism && typeof item.nameOfAct !== 'undefined') { item.title = item.nameOfAct; delete item.nameOfAct }
  else if (typeof item.nameOfAct !== 'undefined') { item.title = item.nameOfAct; delete item.nameOfAct }
  else if (typeof item.subject !== 'undefined') { item.title = item.subject; delete item.subject }

  // type
  if (typeof item.genre !== 'undefined') { item.type = item.genre; delete item.genre }
  else if (typeof item.letterType !== 'undefined') { item.type = item.letterType; delete item.letterType }
  else if (jurism && typeof item.manuscriptType !== 'undefined') { item.type = item.manuscriptType; delete item.manuscriptType }
  else if (typeof item.manuscriptType !== 'undefined') { item.type = item.manuscriptType; delete item.manuscriptType }
  else if (typeof item.mapType !== 'undefined') { item.type = item.mapType; delete item.mapType }
  else if (typeof item.postType !== 'undefined') { item.type = item.postType; delete item.postType }
  else if (typeof item.presentationType !== 'undefined') { item.type = item.presentationType; delete item.presentationType }
  else if (typeof item.reportType !== 'undefined') { item.type = item.reportType; delete item.reportType }
  else if (typeof item.thesisType !== 'undefined') { item.type = item.thesisType; delete item.thesisType }
  else if (typeof item.websiteType !== 'undefined') { item.type = item.websiteType; delete item.websiteType }

  // volume
  if (typeof item.codeVolume !== 'undefined') { item.volume = item.codeVolume; delete item.codeVolume }
  else if (typeof item.reporterVolume !== 'undefined') { item.volume = item.reporterVolume; delete item.reporterVolume }
}

// import & export translators expect different creator formats... nice

export function simplifyForExport(item) {
  unalias(item)

  item.tags = item.tags ? item.tags.map(tag => tag.tag ) : []
  item.notes = item.notes ? item.notes.map(note =>  note.note || note ) : []
  if (item.filingDate) item.filingDate = item.filingDate.replace(/^0000-00-00 /, '')

  if (item.creators) {
    for (const creator of item.creators) {
      if (creator.fieldMode) {
        creator.name = creator.name || creator.lastName
        delete creator.lastName
        delete creator.firstName
        delete creator.fieldMode
      }
    }
  }

  return item
}

export function simplifyForImport(item) {
  unalias(item)

  if (item.creators) {
    for (const creator of item.creators) {
      if (creator.name) {
        creator.lastName = creator.lastName || creator.name
        creator.fieldMode = 1
        delete creator.firstName
        delete creator.name
      }
    }
  }

  return item
}
